	var repo_sysID = workflow.scratchpad.repo_sysID;
	var user = workflow.scratchpad.scm_User;
	var password = workflow.scratchpad.scm_Password;
	var SHA = workflow.inputs.u_scm_commit;
	var repo = workflow.scratchpad.repo;
	var owner = workflow.scratchpad.owner;
	var repo_commits = new GlideRecord('x_snc_itom_ci_'+repo_type+'_commits');
	var files;
	repo_commits.initialize();
	repo_commits.branch = branch_sysID;
	if (workflow.inputs.u_mergeid){
		repo_commits.u_pull_request = workflow.inputs.u_mergeid;
	}
	repo_commits.change_additions = commits_return_json.stats.additions;
	repo_commits.change_deletions = commits_return_json.stats.deletions;
	repo_commits.total_changes = commits_return_json.stats.total;
	repo_commits.repository = repo_sysID;
	if (repo_type == 'github') {
		files = commits_return_json.files;
		repo_commits.author_name = commits_return_json.commit.author.name;
		repo_commits.commit_date = ISODateTimeToGlideDateTime(commits_return_json.commit.author.date);
		repo_commits.commiter_email = commits_return_json.commit.committer.email;
		repo_commits.commiter = commits_return_json.commit.committer.name;
		repo_commits.message = commits_return_json.commit.message;
		repo_commits.sha = commits_return_json.sha;
		repo_commits.url = commits_return_json.url;
	} else if (repo_type == 'gitlab') {
		repo_commits.author_name = commits_return_json.author_name;
		repo_commits.commit_date = ISODateTimeToGlideDateTime(commits_return_json.committed_date);
		repo_commits.commiter_email = commits_return_json.committer_email;
		repo_commits.commiter = commits_return_json.committer_name;
		repo_commits.message = commits_return_json.message;
		repo_commits.sha = commits_return_json.id;
		repo_commits.url = 'https://gitlab.com/' + owner + '/' + repo + '/commit/' + SHA;
		try {
			var commit_diffs = new sn_ws.RESTMessageV2();
			commit_diffs.setEndpoint('https://gitlab.com/api/v4/projects/' + owner + '%2F' + repo + '/repository/commits/' + SHA + '/diff?private_token=' + password);
			commit_diffs.setHttpMethod("get");
			var diff_response = commit_diffs.execute();
			//gs.info("GITLAB DIFFS= "+diff_response.getBody());
			files = global.JSON.parse(diff_response.getBody());
		} catch(ex) {
			gs.info("Some error on the GitLab commit file diff get!");
			gs.info(ex);
		}
	}
	var commit_id = repo_commits.insert();
	workflow.scratchpad.commitID = commit_id;
	//Loop through all files in a commit
	for (ib = 0; ib < files.length; ib++) {

		//Get The RAW file that was modified in the commit
		try {
			var rawfile = new sn_ws.RESTMessageV2();
			if (repo_type == 'github') {
				var raw_filename = files[ib].filename;
				var fileurl = encodeURI(raw_filename);
				//gs.info('ENDPOINT = https://raw.githubusercontent.com/' + owner + "/" + repo + "/" + SHA + "/" + fileurl);
				rawfile.setEndpoint('https://raw.githubusercontent.com/' + owner + "/" + repo + "/" + SHA + "/" + fileurl);
				rawfile.setBasicAuth(workflow.scratchpad.scm_User, workflow.scratchpad.scm_Password);
			} else if (repo_type == 'gitlab') {
				var raw_filename = files[ib].new_path;
				var fileurl = encodeURI(raw_filename);
				//gs.info('ENDPOINT = https://gitlab.com/api/v4/projects/' + owner + '%2F' + repo + '/repository/commits/' + SHA + '/blob/?filepath=' + fileurl);
				rawfile.setEndpoint('https://gitlab.com/api/v4/projects/' + owner + '%2F' + repo + '/repository/commits/' + SHA + '/blob/?private_token=' + password + '&filepath=' + fileurl);
			}
			
			rawfile.setHttpMethod("get");
			var response = rawfile.execute();
			var responseBody = response.getBody();
			var httpStatus = response.getStatusCode();
			//gs.info("RAW FILE :  " + responseBody);
		}
		catch(ex) {
			//var message = ex.getMessage();
			gs.info("Some Error on the raw file get!");
			gs.info(ex);
		}
		//Write the file details to a DB
		var repo_commits_file = new GlideRecord('x_snc_itom_ci_'+repo_type+'_commit_files');
		repo_commits_file.initialize();
		if (repo_type == 'github') {
			repo_commits_file.commit_sha = files[ib].sha;
			repo_commits_file.file_name = files[ib].filename;
			repo_commits_file.file_changes = files[ib].changes;
			repo_commits_file.file_additions = files[ib].additions;
			repo_commits_file.file_deletions = files[ib].deletions;
			repo_commits_file.commit = commit_id;
			repo_commits_file.status = files[ib].status;
			repo_commits_file.total_file_changes = files[ib].changes + files[ib].additions + files[ib].deletions;
			repo_commits_file.file_text = files[ib].patch;
		} else if (repo_type == 'gitlab') {
			try {
				var file_details = new sn_ws.RESTMessageV2();
				//gs.info('BLOB URL = https://gitlab.com/api/v4/projects/' + owner + '%2F' + repo + '/repository/files/?private_token=' + password + '&file_path=' + fileurl +'&ref=' +SHA);
				file_details.setEndpoint('https://gitlab.com/api/v4/projects/' + owner + '%2F' + repo + '/repository/files/?private_token=' + password + '&file_path=' + fileurl +'&ref=' +SHA);
			
				file_details.setHttpMethod("get");
				response = file_details.execute();
				//gs.info ("GITLAB BLOB DETAILS= "+response.getBody());
				var blob_details = global.JSON.parse(response.getBody());
			}
			catch(ex) {
			
				gs.info("Some Error on the GitLab blob details get!");
				gs.info(ex);
			}

			repo_commits_file.commit_sha = blob_details.blob_id;
			repo_commits_file.file_name = blob_details.file_name;
			repo_commits_file.file_size = blob_details.size;
			repo_commits_file.commit = commit_id;
			if (files[ib].new_file) {
				repo_commits_file.status = 'new file';
			} else if (files[ib].renamed_file){
				repo_commits_file.status = 'renamed file';
			} else if (files[ib].deleted_file){
				repo_commits_file.status = 'deleted file';
			} else {
				repo_commits_file.status = 'modified';
			}
			repo_commits_file.file_text = files[ib].diff;	
			repo_commits_file.file_path = files[ib].new_path;
		}
		repo_commits_file.insert();
	}
}
if (branch_commits_InDB){
	//Set a workflow scratch pad value here for the commit SHA
	branch_commits_InDB = branch_CommitsDB.u_sha;
}
