function snTimeFromISOTime(s) {
	var bits = s.split(/[-TZ:+]/g);
	var d = new Date(
		parseInt(bits[0]), 
		parseInt(bits[1]-1),
		parseInt(bits[2]));
	d.setHours(
		parseInt(bits[3]), 
		parseInt(bits[4]), 
		parseInt(bits[5]));
	
	// Get supplied time zone offset in minutes
//	var offsetMinutes = parseInt(bits[6]) * 60 + parseInt(bits[7]);
//	var sign = /\d\d-\d\d:\d\d$/.test(s)? '-' : '+';

	// Apply the sign
//	offsetMinutes = 0 + (sign == '-'? -1 * offsetMinutes : offsetMinutes);

	// Apply offset and local timezone
//	d.setMinutes(d.getMinutes() - offsetMinutes - d.getTimezoneOffset());
	
//	gs.info("rube log: " + d.getUTCFullYear());

	// d is now a local time equivalent to the supplied time
	var dt_string = '';
	dt_string += d.getFullYear();
	dt_string += "-";
	dt_string += d.getMonth() + 1;
	dt_string += "-";
	dt_string += d.getDate();
	dt_string += " ";
	dt_string += d.getHours();
	dt_string += ":";
	dt_string += d.getMinutes();
	dt_string += ":";
	dt_string += d.getSeconds();
	

	return dt_string;
}



